void Move_WayPoints_ToNewPose()
{
    int r = 0;
    int zad = 0;
    int CurrentX = 0;
    int CurrentY = 0;
    float BirdUp = 0;
    float BirdDown = 0;
    for (uint8_t j = 0; j < 16; j++) {

        for (int i = 0; i < WaypointThings[j].index; i++)
        {
            //by defauly waypoints spawn from {0, 0} coordinate
            //chao just teleport them 160 by x and 200 by y
            //" So waypoint 1 is loaded to position (0, 0), and Chao read that as (-160, -200) "
            //" So the waypoints are 10 unit squares, or 1m "
            //for this waypoint repositioning we need to move all waypoints -225 by x -25 by y -> so 1st waypoint will be {-385, 175}
            //between gaps 14 by x -2 by y
            //WaypointThings[j].array[i].x = 0.0f + 14.0f  * i - 488.0f * zad;
            //WaypointThings[j].array[i].z = 0.0f + 2.0f * zad;
            //WaypointThings[j].array[i].x = -225.0f + 14.0f * i - 448.0f * zad;
            //WaypointThings[j].array[i].z = 25.0f + 2.0f * zad;
            CurrentX = round(WaypointThings[j].array[i].x);
            //PrintDebug("OGWayPointXPos %d: %d", i + 1, CurrentX);
            CurrentY = round(WaypointThings[j].array[i].z);
            //PrintDebug("OGWayPointYPos %d: %d", i + 1, CurrentY);
            BirdUp = round(-22.5f + 1.4f * CurrentX);
            BirdDown = round(2.5 + 0.2 * CurrentY);
            WaypointThings[j].array[i].x += BirdUp;
            WaypointThings[j].array[i].z += BirdDown;
            //WaypointThings[j].array[i].z += 2 * WaypointThings[j].array[i].z;
            //WaypointThings[j].array[i].x += -225.0f;
            //WaypointThings[j].array[i].z += 25.0f;

            //PrintDebug("WayPointXPos %d: %d", i+1, WaypointThings[j].array[i].x);
            //PrintDebug("WayPointZPos %d: %d", i+1, WaypointThings[j].array[i].z);
            //PrintDebug("RealWayPointXPos %d: %d", i + 1, (WaypointThings[j].array[i].x - 16) * 10);
            //PrintDebug("RealWayPointZPos %d: %d", i + 1, (WaypointThings[j].array[i].z - 20) * 10);
            //PrintDebug("WayPointIndex: %d", WaypointThings[j].index);
            //PrintDebug("WayPointSelect: %d", i+1);
            //PrintDebug("WayPointJ: %d", j + 1);
            //PrintDebug(" ");

            r++;
            if (r > 31)
            {
                zad++;
                r = 0;
            }
        }
    }
}

