extern "C" {
	// Runs a single time once the game starts up. Required for My Level Mod.
	__declspec(dllexport) void Init(
			const char* modFolderPath,
			const HelperFunctions& helperFunctions) {
		myLevelMod = new LevelImporter(modFolderPath, helperFunctions);
		myLevelModInit(modFolderPath, myLevelMod);

		myLevelMod->importLevel("objLandTableNeut");

		OGCamHeight = flt_1657CEC;
		PrintDebug("Does thit shit work");
		WriteJump((void*)0x52B200, Chao_OOBLimit_r);

        WriteJump((void*)0x54C550, Past_Garden_Manager);

		init_WaterHack();
        initNewWayPoints();
        Init_NewChaoStartPos();
	}
	
	// Runs for every frame while the game is on. Required for My Level Mod.
    __declspec(dllexport) void __cdecl OnFrame() {
		myLevelMod->onFrame();
        if (CurrentChaoArea == 1)
        {
            if (ControllerPointers[0]->on & Buttons_L)
            {
                CamRotateInput += 4;
            }
            if (ControllerPointers[0]->press & Buttons_L)
            {
                //PrintDebug("CharX: %f", MainCharObj1[0]->Position.x);
                //PrintDebug("CharY: %f", MainCharObj1[0]->Position.y);
                //PrintDebug("CharZ: %f", MainCharObj1[0]->Position.z);
            }
            if (ControllerPointers[0]->on & Buttons_R)
            {
                CamRotateInput -= 4;
            }
        }
        if (CamRotateInput > 180)
        {
            CamRotateInput = -180;

            //PrintDebug("CamRotateInput: %f", CamRotateInput);
        }
        if (CamRotateInput < -180)
        {
            CamRotateInput = 180;

            //PrintDebug("CamRotateInput: %f", CamRotateInput);
        }
	}

	// Runs when the game closes. Required for My Level Mod.
	__declspec(dllexport) void __cdecl OnExit() {
		myLevelMod->free();
	}

	__declspec(dllexport) ModInfo SA2ModInfo = { ModLoaderVer };
}

